
public class Println {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	String nom = "Knut";
	String ap = "Waale";
	
		System.out.println("Nueva Linea: \n" + nom + " " + ap);
		System.out.println("Tabulador: \t" + nom + " " + ap);
		System.out.println("Retroceso: \b" + nom + " " + ap);
		System.out.println("Retorno de Carro: \r" + nom + " " + ap);
		System.out.println("Comilla Simple: \'" + nom + " " + ap + "\'");
		System.out.println("Comilla Doble: \"" + nom + " " + ap + "\"");
		System.out.println("Retorno de carro: \r"+ nom +" "+ap);
		
	
	}

}
